# Accept epsilon only if explicitly allowed by start productions
if s == "" and "" in rules.get(start, []):
    return True

# For non-empty strings, try to match productions
def can_derive(nt: str, remaining: str) -> bool:
    for production in rules.get(nt, []):
        if _matches_production(production, remaining, rules):
            return True
    return False

def _matches_production(production: str, remaining: str, rules: Dict[str, List[str]]) -> bool:
    # Base case: empty production
    if production == "":
        return remaining == ""

    # Try to match the first symbol of production
    first_symbol = production[0]
    rest_of_production = production[1:]

    # If first symbol is a terminal
    if not first_symbol.isupper():
        if remaining and remaining[0] == first_symbol:
            return _matches_production(rest_of_production, remaining[1:], rules)
        else:
            return False

    # If first symbol is a nonterminal
    # Try all possible splits of the remaining string
    for i in range(len(remaining) + 1):
        prefix = remaining[:i]
        suffix = remaining[i:]
        if can_derive(first_symbol, prefix):
            if _matches_production(rest_of_production, suffix, rules):
                return True
    return False

return can_derive(start, s)
